import csvclass Robot(object):    def __init__(self, name):        self.name = name                    def say_hello(self):        print('###################################')        print('こんにちは！', '私は', self.name, 'です')        print('あなたの名前はなんですか？')        def reading_csv(self):        with open('restaurant.csv') as read_csv:            reader = csv.DictReader(read_csv)            restaurant_name = []            for row in reader:                restaurant_name.append(row)            return restaurant_name                def writing_csv(self,favorite_restaurant):        fieldnames = ['Name', 'Count']        with open('restaurant.csv', 'a') as csv_file:            writer = csv.DictWriter(csv_file, fieldnames = fieldnames)            writer.writerow({'Name': favorite_restaurant, 'Count': 1})                def recommend_rs(self, restaurant_name, r_count):        if r_count >= 3:            r_count = 2        for res in restaurant_name[0:r_count]:            print('私のオススメのレストランは', res['Name'], 'です')            print('このレストランはすきですか？[Yes/No]')            user_answer = input('YesかNoと入力')            if user_answer == 'Yes':                res['Count'] = int(res['Count']) + 1                # print(restaurant_name)                self.add_csv(restaurant_name)                break            elif user_answer == 'No':                print('かしこまりました')                        def add_csv(self, restaurant_name):        fieldnames = ['Name', 'Count']        with open('restaurant.csv', 'w') as csv_file:            writer = csv.DictWriter(csv_file, fieldnames = fieldnames)            writer.writeheader()            for row in restaurant_name:                writer.writerow(row)                    def check_restaurant(self,restaurant_name,user_name):        r_count = len(restaurant_name)        # print(r_count)        if r_count > 3:                        self.recommend_rs(restaurant_name, r_count)        elif r_count <= 2:            self.recommend_rs(restaurant_name, r_count)        self.add_restaurant(user_name,restaurant_name)        self.good_bye(user_name)         def add_restaurant(self,user_name,restaurant_name):        print(user_name, 'さん', 'どこのレストランが好きですか？')        input_restaurant = input()        favorite_restaurant = input_restaurant.capitalize()        #ここに入力されたレストラン名があるとき、カウントをあげる処理を追加する。        for res in restaurant_name:            if res['Name'] == favorite_restaurant:                res['Count'] = int(res['Count']) + 1                self.add_csv(restaurant_name)                return            else:                continue        #ここまで        #カウント追加後に追記されてほしくない。        self.writing_csv(favorite_restaurant)         print(favorite_restaurant, 'が好きなのですね！')        def good_bye(self,user_name):        print(user_name, 'さん', 'ありがとうございました。')        print('良い一日を！さようなら。')        